mapper.select:
select

from cskaoyan_mall_issue

  deleted = 0

    and question like CONCAT('%', #{conditions.question}, '%')


ORDER BY ${pageDetail.sort} ${pageDetail.order}
limit #{pageDetail.limit} offset #{pageDetail.offset}

mapper.update:
update cskaoyan_mall_issue
set deleted = true
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into cskaoyan_mall_issue (question, answer,
  add_time, update_time, deleted
  )
values (#{question,jdbcType=VARCHAR}, #{answer,jdbcType=VARCHAR},
  now(), now(), false
  )
